const { DataTypes } = require('sequelize');
const { sequelize } = require('../config/db');
const User = require('./user');  // pastikan import User dengan benar

// Model Comment
const Comment = sequelize.define('Comment', {
  id: {
    type: DataTypes.INTEGER,
    autoIncrement: true,
    primaryKey: true
  },
  userId: {
    type: DataTypes.TEXT,
    allowNull: false,
    field: 'user_id',
    references: {
      model: User,  // memastikan ini mengacu ke model User
      key: 'id'
    }
  },
  title: {
    type: DataTypes.TEXT,
    allowNull: false
  },
  artist: {
    type: DataTypes.TEXT,
    allowNull: false
  },
  rating: {
    type: DataTypes.INTEGER,
    allowNull: false,
    validate: {
      min: 1,
      max: 5
    }
  },
  message: {
    type: DataTypes.TEXT,
    allowNull: false
  }
}, {
  timestamps: true,  // otomatis buat created_at dan updated_at
  tableName: 'comments',  // pastikan ini sesuai dengan nama tabel di PostgreSQL
  underscored: true  // memastikan kolom di database menggunakan snake_case (user_id)
});

// Relasi ke User
Comment.belongsTo(User, { foreignKey: 'userId', as: 'user' });

module.exports = Comment;
